import turtle
turtle.setup(400,400)
turtle.pencolor('Red')
turtle.penup()
turtle.goto(-100,75)
turtle.pendown()
turtle.forward(100)
turtle.right(90)
turtle.forward(50)
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)