import turtle
turtle.setup(400,400)
turtle.pencolor('green')
turtle.penup()
turtle.goto(-150,100)
turtle.pendown()
turtle.forward(100)
turtle.right(90)
turtle.forward(50)
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)

turtle.pencolor('red')
turtle.penup()
turtle.goto(-100,50)
turtle.pendown()
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)

turtle.pencolor('blue')
turtle.penup()
turtle.goto(-50,0)
turtle.pendown()
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)
turtle.right(90)
turtle.forward(100)
turtle.right(90)
turtle.forward(50)

turtle.pencolor('black')
turtle.penup()
turtle.goto(-150,100)
turtle.pendown()
turtle.right(90)
turtle.forward(200)
turtle.right(90)
turtle.forward(150)
turtle.right(90)
turtle.forward(200)
turtle.right(90)
turtle.forward(150)

turtle.pencolor('black')
turtle.penup()
turtle.goto(-50,-100)
turtle.pendown()
turtle.right(90)
turtle.write('A108222040_潘凌徵')



import turtle
turtle.pencolor('black')
turtle.pensize(5)
turtle.circle(36)

turtle.penup()
turtle.goto(-80,0)
turtle.pendown()
turtle.pencolor('blue')
turtle.circle(36)

turtle.penup()
turtle.goto(80,0)
turtle.pendown()
turtle.pencolor('red')
turtle.circle(36)

turtle.penup()
turtle.goto(50,-30)
turtle.pendown()
turtle.pencolor('green')
turtle.circle(36)

turtle.penup()
turtle.goto(-50,-30)
turtle.pendown()
turtle.pencolor('yellow')
turtle.circle(36)

turtle.pencolor('black')
turtle.penup()
turtle.goto(0,-70)
turtle.pendown()
